(358, 'movie', 'Halloweentown High', '', '', '786936281835', NULL, '', 'Marnie Piper prepares to begin a new school year, she asks the Halloweentown Hot Witches\' Council to work toward openness between Halloweentown and the mortal world. She proposes to bring a group of Halloweentown students to her own high school in the mortal world.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-06-22 17:31:52', '2025-06-22 17:31:52'),
(359, 'movie', 'Hammer Film Collection: Volume 2', '', '', '683904544636', NULL, '', 'This collection contains: \n<b>  - The Revenge of Frankenstein (1958)</b>\n<b>  - The Snorkel (1958)</b>\n<b>  - Never Take Sweets From A Stranger (1960)</b>\n<b>  - Maniac (1963)</b>\n<b>  - Die Die My Darling (1965)</b>\n<b>  - Creatures The World Forgot (1971)</b>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-06-22 17:31:52', '2025-06-22 17:31:52'),
