(1096, 'movie', 'Transformers', '', '', '097363455349', NULL, '', 'Young teenager Sam Witwicky becomes involved in the ancient struggle between two extraterrestrial factions of transforming robots – the heroic Autobots and the evil Decepticons. Sam holds the clue to unimaginable power and the Decepticons will stop at nothing to retrieve it.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-06-22 17:31:54', '2025-06-22 17:31:54'),
(1097, 'movie', 'Transformers 2: Revenge of the Fallen', '', '', '097360724240', NULL, '', 'Sam Witwicky leaves the Autobots behind for a normal life. But when his mind is filled with cryptic symbols, the Decepticons target him and he is dragged back into the Transformers\' war.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-06-22 17:31:54', '2025-06-22 17:31:54'),
(1098, 'movie', 'Transformers: Dark of the Moon', '', '', '097360810042', NULL, '', 'The Autobots continue to work for NEST, now no longer in secret. But after discovering a strange artifact during a mission in Chernobyl, it becomes apparent to Optimus Prime that the United States government has been less than forthright with them.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-06-22 17:31:54', '2025-06-22 17:31:54'),
