(1203, 'movie', 'Beau Is Afraid', '', '', '031398339991', NULL, '', 'Following the sudden death of his mother, a mild-mannered but anxiety-ridden man confronts his darkest fears as he embarks on an epic, Kafkaesque odyssey back home.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-06-22 17:31:54', '2025-06-22 17:31:54'),
(1204, 'movie', 'Mallrats', '', '', '760137124849', NULL, '', 'Both dumped by their girlfriends, two best friends seek refuge in the local mall. Eventually, they decide to try and win back their significant others and take care of their respective nemeses.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-06-22 17:31:54', '2025-06-22 17:31:54'),
